import React, { Component } from 'react';
import { Route } from 'react-router-dom';
import { Home, Auth } from './pages';
import Homepage from './components/Home';
import SignIn from './components/SignIn/SignIn';
import HeaderContainer from './containers/Base/HeaderContainer';
import { makeStyles } from '@material-ui/core';
import clsx from 'clsx';
import DrawerContainer from './components/Base/Drawer/DrawerContainer';

function App() {
  const [open, setOpen] = React.useState(false);
  console.log('0:' + open);
  const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex'
    },
    paper: {
      padding: theme.spacing(2),
      display: 'flex',
      overflow: 'auto',
      flexDirection: 'column'
    }
  }));
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };
  const classes = useStyles();
  return (
    <div>
      <div className={classes.root}>
        <HeaderContainer open={open} handleDrawerOpen={handleDrawerOpen}></HeaderContainer>
        <DrawerContainer open={open} handleDrawerClose={handleDrawerClose}></DrawerContainer>
      </div>
      <Route path="/auth" component={Auth} />
      {/* <Route path="/" component={Homepage} exact /> */}
      <Route path="/signIn" component={SignIn} />
      <Route exact path="/homepage" component={Homepage} />
    </div>
  );
}

export default App;
