import React, { useEffect, useState } from 'react';

import { makeStyles, Typography } from '@material-ui/core';
import Link from '@material-ui/core/Link';
import moment from 'moment';

import Title from './Title';

const preventDefault = (event) => {
  event.preventDefault();
};

const useStyles = makeStyles({
  depositContext: {
    flex: 1
  }
});

const Today = () => {
  const [currentTime, setCurrentTime] = useState(moment());
  let timer = null;
  useEffect(() => {
    timer = setInterval(() => {
      setCurrentTime(moment());
    }, 1000);
    return () => {
      clearInterval(timer);
    };
  }, []);

  const classes = useStyles();
  return (
    <React.Fragment>
      <Title>Today</Title>
      {/* 현재시각 : 시계처럼 만들기 */}
      <Typography component="p" variant="h3" color="primary">
        {currentTime}
      </Typography>
      <Typography component="p" variant="h5">
        {/* 오늘 날짜 가져오기 */}
        {'on '}
        {moment().format('DD')}
        {', '}
        {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'][moment().format('M') - 1]}
        {', '}
        {moment().format('YYYY')}
      </Typography>
      <Typography color="textSecondary" className={classes.depositContext}>
        {/* dday 가져오기 */}
        {'created Date D'}
        {moment('2021-02-19').diff(moment().format('YYYY-MM-DD', 'days'), 'days')}
      </Typography>
      {/* 클릭 구현할거 생각하기 */}
      <div>
        <Link color="primary" href="#" onClick={preventDefault}>
          Click here.
        </Link>
      </div>
    </React.Fragment>
  );
};

export default Today;
