/**
 * html > body > iframe > html > body >
 */

const axios = require('axios');
const cheerio = require('cheerio');

// axios 를 활용해서 AJAX로 HTML 문서를 가져온다.
const getHTML = async () => {
  try {
    return await axios.get('https://blog.naver.com/PostList.nhn?blogId=wlsekffo1674&widgetTypeCall=true&directAccess=true');
  } catch (e) {
    console.error(e);
  }
};

exports.getHTMLResults = getHTML()
  .then((html) => {
    let result = [];
    const $ = cheerio.load(html.data);
    result['title'] = $('body').find('.pcol1').text();
    return result;
  })
  .then((res) => console.log(res));

console.log('result' + getHTMLResults());
